import React, {Component, useState} from 'react';
import moment from 'moment';
import { Button, Header, Image, Modal, Form, Select, Step } from 'semantic-ui-react'
import DatePicker from 'react-datepicker'
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome'
import {faCalendar} from '@fortawesome/free-solid-svg-icons'




import '../css/pickerStyle.scss'
import "react-datepicker/dist/react-datepicker.css"

class AddModal extends Component {
    constructor(props){
    
      super(props);
      
      this.state ={
        firstname:'',
        lastname:'',
        patientcontact:'',
        process:'',
        notes:'',
        date: moment().toDate(),
        time: [],
        doctor:[],
        open: false,
      }
      this.setOpen = this.setOpen.bind(this);
      this.handleChange= this.handleChange.bind(this);
      this.handleDate = this.handleDate.bind(this);
    }

    //function for opening and closing the modal
    setOpen(){
      this.setState({
        open: !this.setState.open
      }) 
    }

    //Function for submitting values
    handleChange = (e, {name, value}) =>{
      this.setState({
        [name]:value
      })
      console.log(this.state.date)
    }
    handleSubmit=()=>{
      const user = {
        firstname:'',
        lastname:'',
        patientcontact:'',
        process:'',
        notes:'',
        date: '',
        time: '',
        doctor:'',
      }
      this.setState({
        //Axios: Connects to DB and sends the data
      })
      
    } 

    //Datepicker change
    handleDate(date){
      this.setState({
        date:date
      })
    }
    

    
    
  
    render(){
      return (
        <div class="item">
          <Modal
            onClose={this.setOpen}
            onOpen={this.setOpen}
            as={Form}
            open={this.state.open}
            onSubmit={this.handleSubmit}
            trigger={
            <div class="circular ui pink icon button with tooltip" data-title="Add (ENTER)" data-content="Adds an appointment" data-variation="basic" data-position="bottom center" id="add-button">
            <i class="large plus icon"></i>
            </div>}
          >
          <div class="header">
          <Step.Group fluid>
              <Step active>
                <i class="calendar icon"></i>
                    <div class="content">
                        <div class="title">Date</div>
                    </div>
              </Step>
              <Step>
                <i class="address card icon"></i>
                    <div class="content">
                        <div class="title">Appointment</div>
                    </div>
              </Step>
          </Step.Group>
          </div>
          <Modal.Content>
            <Form.Group>
                  <FontAwesomeIcon icon={faCalendar} className="iconCalendar"/>
                  <DatePicker calendarClassName="red-border"
                    selected={this.state.date}
                    onChange={this.handleDate}
                    />
            </Form.Group>
          </Modal.Content>
          </Modal>
        </div>
        
      )
    }
  }
export default AddModal
